def get_attributes(self):
    return self.__dict__


def json_as_dict_to_objects(json_as_dict: dict, return_object=None):
    if return_object is None:
        return_object = type('root', (object,), {'get_attributes': get_attributes})
        return_object = return_object()
    for index, (key, val) in enumerate(json_as_dict.items()):
        if isinstance(val, (int, str, float, bool)):
            setattr(return_object, key, val)
        elif isinstance(val, dict):
            new_obj = type(key, (object,), {'get_attributes': get_attributes})
            new_obj = new_obj()
            setattr(return_object, key, json_as_dict_to_objects(val, new_obj))
        elif isinstance(val, list):
            for i, el in enumerate(val):
                # тут еще должна быть реализация обработки массива JSON
                pass
    return return_object

